-- # 个股日线基础因子数据表
drop table if exists `factor_basic_info`;
CREATE TABLE `factor_basic_info` ( 
  `name` varchar(256) NOT NULL COMMENT '因子名称', 
  `desc` text DEFAULT NULL COMMENT '因子描述',
  `source` VARCHAR(256) NOT NULL COMMENT '因子源',
  `type` int DEFAULT NULL COMMENT '因子类型',
  `scope` int DEFAULT NULL COMMENT '范围',
  `status` int DEFAULT NULL COMMENT '状态',
  `remark` text DEFAULT NULL COMMENT '备注',
  `update_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `create_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`name`,`source`),
  INDEX `IDX_CODE` (`name`),
  INDEX `IDX_DATE` (`source`),
  INDEX `IDX_ISST` (`status`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;




